      \begin{align*} \boldsymbol {Q}_{1}(\boldsymbol {J}_{i},\boldsymbol {J}_{j})=&\left ({\sqrt {2\pi }}\right)^{-n}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}\int _{-\infty }^{+\infty }\frac {\partial \phi (\boldsymbol {x},\boldsymbol {J}_{i})}{\partial \boldsymbol {J}_{i}} \\&\times \,\frac {\partial \phi (\boldsymbol {x},\boldsymbol {J}_{j})}{\partial \boldsymbol {J}_{j}^{T}} \exp \!\left ({\!-\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {\Sigma }^{-1}\boldsymbol {x}\!}\right)\!\mathrm {d}\boldsymbol {x} \\=&{\frac {2}{\pi }}\left ({\sqrt {2\pi }}\right)^{-n}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}\!\!\!\int _{-\infty }^{+\infty }\!\!\!\boldsymbol {x}\boldsymbol {x}^{T}\exp \!\!\left ({\!\!-\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}\boldsymbol {x}\!}\right)\! \\&\times \,\exp \!\left ({\!-\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {J}_{j}\boldsymbol {J}_{j}^{T}\boldsymbol {x}\!}\right)\! \exp \left ({-\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {\Sigma }^{-1}\boldsymbol {x}}\right)\mathrm {d}\boldsymbol {x} \\=&{\frac {2}{\pi }}\!\left ({\!\sqrt {2\pi }\!}\right)\!^{-n}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}\!\!\int _{-\infty }^{+\infty }\!\!\boldsymbol {x}\boldsymbol {x}^{T} \\&\times \exp \!\!\left ({\!\!-\frac {1}{2}\boldsymbol {x}^{T}(\boldsymbol {\Sigma }^{-1}+\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}+\boldsymbol {J}_{j}\boldsymbol {J}_{j}^{T})\boldsymbol {x}}\right)\mathrm {d}\boldsymbol {x} \\=&{\frac {2}{\pi }}\left ({\sqrt {2\pi }}\right)^{-n}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}\int _{-\infty }^{+\infty }\boldsymbol {x}\boldsymbol {x}^{T} \\&\times \,\exp \left ({-\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {A}(\boldsymbol {J}_{i},\boldsymbol {J}_{j})^{-1}\boldsymbol {x}}\right)\mathrm {d}\boldsymbol {x} \\=&{\frac {2}{\pi }}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}|\boldsymbol {A}(\boldsymbol {J}_{i},\boldsymbol {J}_{j})|^{\frac {1}{2}}\left ({\sqrt {2\pi }}\right)^{-n}|\boldsymbol {A}(\boldsymbol {J}_{i},\boldsymbol {J}_{j})|^{