   \begin{align*} Q_{2}(\boldsymbol {J}_{i},\boldsymbol {J}_{j})=&\left ({\sqrt {2\pi }}\right)^{-n}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}\int _{-\infty }^{+\infty }\frac {\partial \phi (\boldsymbol {x},\boldsymbol {J}_{i})}{\partial \boldsymbol {J}_{i}} \\&\times \,\phi (\boldsymbol {x},\boldsymbol {J}_{j})\exp \left ({-\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {\Sigma }^{-1}\boldsymbol {x}}\right)\mathrm {d}\boldsymbol {x} \\=&\sqrt {\frac {2}{\pi }}\!\left ({\!\sqrt {2\pi }\!}\right)\!^{-n}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}\!\!\!\!\int _{-\infty }^{+\infty }\!\!\!\!\boldsymbol {x}\exp \left ({-\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}\boldsymbol {x}}\right) \\&\times \,\phi (\boldsymbol {x},\boldsymbol {J}_{j})\exp \!\left ({\!-\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {\Sigma }^{-1}\boldsymbol {x}\!}\right)\!\mathrm {d}\boldsymbol {x} \\=&\sqrt {\frac {2}{\pi }}\!\left ({\!\sqrt {2\pi }}\right)^{-n}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}\int _{-\infty }^{+\infty }\boldsymbol {x}\phi (\boldsymbol {x},\boldsymbol {J}_{j}) \\&\times \,\exp \!\left ({\!-\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {B}(\boldsymbol {J}_{i})\boldsymbol {x}}\right)\mathrm {d}\boldsymbol {x} \\=&\sqrt {\frac {2}{\pi }}\left ({\sqrt {2\pi }}\right)^{-n}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}\boldsymbol {B}(\boldsymbol {J}_{i})^{-1} \\&\times \,\int _{-\infty }^{+\infty }\phi (\boldsymbol {x},\boldsymbol {J}_{j})\mathrm {d}\exp \left ({-\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {B}(\boldsymbol {J}_{i})\boldsymbol {x}}\right) \\=&\sqrt {\frac {2}{\pi }}\left ({\sqrt {2\pi }}\right)^{-n}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}\boldsymbol {B}(\boldsymbol {J}_{i})^{-1} \\&\times \phi (\boldsymbol {x},\boldsymbol {J}_{j})\exp \left ({\frac {1}{2}\boldsymbol {x}^{T}\boldsymbol {B}(\boldsymbol {J}_{i})\boldsymbol {x}}\right)\Big \vert _{-\infty }^{+\infty } \\&+\,\frac {2}{\pi }\left ({\sqrt {2\pi }}\right)^{-n}|\boldsymbol {\Sigma }|^{-\frac {1}{2}}\boldsymb