where:

   \begin{align*} \boldsymbol {B}(\boldsymbol {J}_{i})^{-1}=&\left ({\boldsymbol {\Sigma }^{-1}+\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}}\right)^{-1} \\=&\boldsymbol {\Sigma }-\frac {\boldsymbol {\Sigma }\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}\boldsymbol {\Sigma }}{1+\boldsymbol {J}_{i}^{T}\boldsymbol {\Sigma }\boldsymbol {J}_{i}}. \tag{A-8}\\ \boldsymbol {A}(\boldsymbol {J}_{i},\boldsymbol {J}_{j})=&\left ({\boldsymbol {B}(\boldsymbol {J}_{i})+\boldsymbol {J}_{j}\boldsymbol {J}_{j}^{T}}\right)^{-1} \\=&\boldsymbol {B}(\boldsymbol {J}_{i})^{-1}-\frac {\boldsymbol {B}(\boldsymbol {J}_{i})^{-1}\boldsymbol {J}_{j}\boldsymbol {J}_{j}^{T}\boldsymbol {B}(\boldsymbol {J}_{i})^{-1}}{1+\boldsymbol {J}_{j}^{T}\boldsymbol {B}(\boldsymbol {J}_{i})^{-1}\boldsymbol {J}_{j}} \\=&\boldsymbol {\Sigma }-\frac {\boldsymbol {\Sigma }\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}\boldsymbol {\Sigma }}{1+\boldsymbol {J}_{i}^{T}\boldsymbol {\Sigma }\boldsymbol {J}_{i}} \\&-\,\frac {\left ({\boldsymbol {\Sigma }-\frac {\boldsymbol {\Sigma }\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}\boldsymbol {\Sigma }}{1+\boldsymbol {J}_{i}^{T}\boldsymbol {\Sigma }\boldsymbol {J}_{i}}}\right)\boldsymbol {J}_{j}\boldsymbol {J}_{j}^{T}\left ({\boldsymbol {\Sigma }-\frac {\boldsymbol {\Sigma }\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}\boldsymbol {\Sigma }}{1+\boldsymbol {J}_{i}^{T}\boldsymbol {\Sigma }\boldsymbol {J}_{i}}}\right)}{1+\boldsymbol {J}_{j}^{T}\left ({\boldsymbol {\Sigma }-\frac {\boldsymbol {\Sigma }\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}\boldsymbol {\Sigma }}{1+\boldsymbol {J}_{i}^{T}\boldsymbol {\Sigma }\boldsymbol {J}_{i}}}\right)\boldsymbol {J}_{j}} \\=&(\boldsymbol {\Sigma }^{-1}+\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}+\boldsymbol {J}_{j}\boldsymbol {J}_{j}^{T})^{-1} =\boldsymbol {\Sigma }-\boldsymbol {\Sigma } \\&\times \left ({\frac {(1+\boldsymbol {J}_{j}^{T}\boldsymbol {\Sigma }\boldsymbol {J}_{j})\boldsymbol {J}_{i}\boldsymbol {J}_{i}^{T}+(1+\boldsymbol {J}_{i}^{T}\boldsymbol {\